import React, {Component} from 'react';
import {Scatter} from 'react-chartjs-2';
import _ from 'lodash';

const coil_strength = 0.125;
const frequency = 50;
const time_cycle = 2;
const graph_x = 10;

//set the amount of calculated points with defining the precisionStep.
const precisionStep = 0.05;


class Chart extends Component {

  constructor(props) {
    super(props);
    this.state = {
      countToThirty: 0,
      counter: 0,
      scatterChartData: {},
      savestate:{},
    };


    this.plotGraphs = this.plotGraphs.bind(this);
    //this.getLineCoordinates = this.getLineCoordinates.bind(this);
  }

//Creating a range with Lodash
  // getInterval = function (start, end, steps){
  //   return _.range(start, end, steps);
  // } 


  getLineCoordinates = () => _.range(0 , graph_x, precisionStep).map(x =>  {

    console.log(`x: ${_.floor(x,3)}, y: ${this.calculate(_.ceil(x,3))}`);
      return {
        x: _.floor(x,3), //Rounding down
        y: this.calculate(_.ceil(x,3)) //Rounding up
      }
    })

  

  plotGraphs() {

     const redDot = {
       label: 'Red Dot',
       fill: false,
       backgroundColor: 'rgb(255, 99, 132)', //red
       borderColor: 'rgb(255, 99, 132)',
       pointStyle: 'circle', //"square", "cross", "none"
       pointRadius: 5,


       data: [{
         x: this.props.time%10, //0,1,2,3,4,5,6,7,8 or 9
         y: this.calculate(this.props.time%10)
       }]}


    if(this.state.countToThirty === 0 ){
      console.log("count = 0");

    const chartFunction = {

      lineTension : 0,
      label: 'Function',
      fill: false,

      backgroundColor: 'rgb(54, 162, 235)', //blue
      borderColor: 'rgb(54, 162, 235)',
      data: this.getLineCoordinates()
    };

    this.setState({
      savestate: this.getLineCoordinates(),
      scatterChartData: {
        datasets: [
          redDot,
          chartFunction,

        ]
      }
    })
  }


  else{
    console.log("normalrender");

  const chartFunction = {
    legend: {
      display: true,
  },

    lineTension : 0,
    label: 'Function',
    fill: false,
    backgroundColor: 'rgb(54, 162, 235)', //blue
    borderColor: 'rgb(54, 162, 235)',
    data: this.state.savestate
  };
  this.setState({
    scatterChartData: {
      datasets: [
        redDot,
        chartFunction,

      ]
    }
  })}

  }
  componentDidMount() {
    this.timer = setInterval(
     () => this.increment(),
     200


)
}
componentWillUnmount() {
clearInterval(this.timer)
}
increment() {
  this.setState({counter: this.state.counter + 0.2})
  this.plotGraphs();
/*
  if ((this.props.time%10 )== 0){
    this.setState({
      countToThirty: this.state.countToThirty + 10
    })
  }
*/

    if(this.state.countToThirty <9.7){
      let x = this.state.countToThirty + 0.2;
      this.setState({countToThirty: x });
    }else{
      this.setState({countToThirty: 0});
    }


}

  //berechnet punkte für "Oxidation"
  /*calculate = (xValue, n = 1) => {
    if(xValue%T<DT){
      return(Ue-Ua)
    }else return -Ua
  };*/
  calculate = (xValue) => {
    var rateTime = time_cycle*this.props.dutyCycle;
    var delta = (this.props.dutyCycle*(1-this.props.dutyCycle)*this.props.voltage)/(coil_strength*frequency);
    var IL_max = (this.props.current*rateTime) +(delta/2)//korrekt
    var IL_min = (this.props.current*rateTime) -(delta/2)//Korrekt



    switch(this.props.part){
      case "Us in V":{
        return  ((xValue%time_cycle)<=rateTime)?  this.props.voltage : 0;

      }
      case  "Ul in V":{
          return  ((xValue%time_cycle)<=rateTime)? (this.props.voltage*time_cycle)-(this.props.voltage*rateTime) : -(this.props.voltage*rateTime);

      }
      case "Ud in V":{
          return  ((xValue%time_cycle)<=rateTime)? 0 : this.props.voltage  ;

      }
      case "Is in A":{
          return (((xValue%time_cycle)<=rateTime)? ( IL_min + (IL_max*(xValue%time_cycle))) : 0);

      }
      case  "Il in A":{
          return (((xValue%time_cycle)<=rateTime)? ( IL_min + (IL_max*((xValue%time_cycle)/rateTime))) : ( IL_min + (IL_max-((((xValue%time_cycle)-rateTime)/(time_cycle-rateTime)*IL_max)))));

      }
      case "Id in A":{
          return (((xValue%time_cycle)<=rateTime)? 0 : ( IL_min + (IL_max*(time_cycle-(xValue%time_cycle)))));
      }
    }
  };


  render() {

    return (
      <div className = "chart" >
      <Scatter data = {this.state.scatterChartData}
      options = {
        {
          tooltips: {
            enabled: false
          },
          legend: {
            display: false
        },
          animation: {
            duration: 0 //general animation time. Was: 140
          },
          hover: {
            animationDuration: 0 // duration of animations when hovering an item
        },
          showLines: true,
          scales: {
            xAxes: [{
              scaleLabel: {
                display: true,
                labelString: 'Time'
              },
              gridLines: {
                drawTicks: true
              },
              type: 'time',
              time: {
                    unit: 'minute',
                    source: 'data'
                },
              ticks:{
                maxRotation: 0,
                beginAtZero: true,
                min:0,
                max:10,
              }
            }],
            yAxes: [{
              ticks: {
              //  min: -500,
              //  max: 500
              },
              scaleLabel: {
                display: true,
                labelString: this.props.part,
              }
            }]
          },
          elements: {
            point: {
              radius: 1
            }
          }
        }
      }
      />
      </div>
    )
  }
}

export default Chart;
